binding.script:
if (getprop("controls/electric/APU-generator") == 0) {
if (getprop("controls/APU/off-start-run") == 2)
	setprop("controls/electric/APU-generator", 1);
else
	screen.log.write("Please turn the APU on first...", 1, 0, 0);
} else {
	setprop("controls/electric/APU-generator", 0);
}

if (getprop("controls/electric/external-power") == 0) {
if (getprop("/services/ext-pwr/enable") == 1)
	setprop("controls/electric/external-power", 1);
else
	screen.log.write("Please Hook the external power cable up first...", 1, 0, 0);
} else {
	setprop("controls/electric/external-power", 0);
}

setprop("/controls/pneumatic/pack1", 1);
setprop("/controls/pneumatic/pack2", 1);
setprop("/controls/pneumatic/e1-bleed", 1);
setprop("/controls/pneumatic/e2-bleed", 1);
setprop("/controls/pneumatic/apu-bleed", 0);
setprop("/controls/pneumatic/triml", 0);
setprop("/controls/pneumatic/trimr", 0);

if (getprop("/velocities/airspeed-kt") > 180) {
if (getprop("/controls/engines/engine[0]/starter") == 1) {
setprop("/controls/engines/engine[0]/starter", 0);
} else {
setprop("/controls/engines/engine[0]/starter", 1);
}
}
if ((getprop("/controls/engines/eec1") == 1) and (getprop("/controls/engines/engine[0]/starter") == 0)) {
setprop("/controls/engines/engine[0]/starter", 1);
} else {
setprop("/controls/engines/engine[0]/starter", 0);
}

if (getprop("/velocities/airspeed-kt") > 180) {
if (getprop("/controls/engines/engine[1]/starter") == 1) {
setprop("/controls/engines/engine[1]/starter", 0);
} else {
setprop("/controls/engines/engine[1]/starter", 1);
}
}
if ((getprop("/controls/engines/eec1") == 1) and (getprop("/controls/engines/engine[1]/starter") == 0)) {
setprop("/controls/engines/engine[1]/starter", 1);
} else {
setprop("/controls/engines/engine[1]/starter", 0);
}
